import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Profile.css'

const Profile = ({ loginStatus }) => {
  const [profileData, setProfileData] = useState(null);
  useEffect(() => {
    // Function to fetch student profile data from the API
    if (loginStatus && loginStatus.studentid) {
      axios.post('http://localhost:8800/studentprofile', {
        sid: loginStatus.studentid // Use loginstatus.studentid directly here
      })
        .then((response) => {
         setProfileData(response.data[0])
        })
        .catch((error) => {
          console.log("Error occurred");
        });
    }
  }, [loginStatus]); // Add loginstatus to the dependency array

  const formatDateOfBirth = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };
  return (
    <div className='profileContainer'>
      <h1 className='profileHead'>Student Profile</h1>
      {profileData ? (
        <div className='profileDetails'>
          <div className='detailContent'><p>Student ID:</p><p>{profileData.studentid}</p></div>
          <div className='detailContent'><p>Name:</p><p>{profileData.name}</p></div>
          <div className='detailContent'><p>Email:</p><p>{profileData.email}</p></div>
          <div className='detailContent'></div>
          <div className='detailContent'></div>
          <div className='detailContent'></div>
          <div className='detailContent'></div>
          <div className='detailContent'></div>
          
          
          
          
          <p>Mess-Name:</p><p>{profileData.messname}</p>
          <p>Semester:</p><p>{profileData.semester}</p>
          <p>Department:</p><p>{profileData.department}</p>
          <p>Preference:</p><p>{profileData.preference}</p>
        </div>
      ) : (
        <p>Loading student profile...</p>
      )}
    </div>
  );
};

export default Profile;
