import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Profile.css'

const Profile = ({ loginStatus }) => {
  const [profileData, setProfileData] = useState(null);
  useEffect(() => {
    // Function to fetch student profile data from the API
    if (loginStatus && loginStatus.studentid) {
      axios.post('http://localhost:8800/studentprofile', {
        sid: loginStatus.studentid // Use loginstatus.studentid directly here
      })
        .then((response) => {
         setProfileData(response.data[0])
        })
        .catch((error) => {
          console.log("Error occurred");
        });
    }
  }, [loginStatus]); // Add loginstatus to the dependency array

  const formatDateOfBirth = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };
  return (
    <div className='profileContainer'>
      <h1 className='profileHead'>Student Profile</h1>
      {profileData ? (
        <div className='profileDetails'>
          <div className='greyContainer'>
            
          </div>
          <div className='detailContent'><p className='greyContent'>Student ID:</p><p className='pinkData'>{profileData.studentid}</p></div>
          <div className='detailContent'><p className='greyContent'>Name:</p><p className='pinkData'>{profileData.name}</p></div>
          <div className='detailContent'><p className='greyContent'>Email:</p><p className='pinkData'>{profileData.email}</p></div>
          <div className='detailContent'><p className='greyContent'>Date of Birth:</p><p className='pinkData'>{formatDateOfBirth( profileData.dob)}</p></div>
          <div className='detailContent'><p className='greyContent'>Mess-Name:</p><p className='pinkData'>{profileData.messname}</p></div>
          <div className='detailContent'><p className='greyContent'>Semester:</p><p className='pinkData'>{profileData.semester}</p></div>
          <div className='detailContent'><p className='greyContent'>Department:</p><p className='pinkData'>{profileData.department}</p></div>
          <div className='detailContent'><p className='greyContent'>Preference:</p><p className='pinkData'>{profileData.preference}</p></div>
        </div>
      ) : (
        <p>Loading student profile...</p>
      )}
    </div>
  );
};

export default Profile;
