import React from "react";

const Notification = () => {
  const [newsItems, setNewsItems] = useState([]);

  //Useeffect for news
  useEffect(() => {
    axios
      .get("http://localhost:8800/news")
      .then((response) => {
        setNewsItems(response.data);
      })
      .catch((error) => {
        console.log("Error occurred while fetching news(frontend):", error);
      });
  }, []);
  return (
    <div>
      <h1>Notification</h1>
      <div>
        <table>
          <thead>
            <tr>
              <th>Sl no.</th>
              <th>Notifications</th>
            </tr>
          </thead>
          <tbody>
            {newsItems.map((item) => {
              <tr key={item.id}>
                <td>{item.content}</td>
              </tr>;
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Notification;
